import { useState } from 'react';

import { HeroContainer, HeroBg, VideoBg, HeroContent, HeroH1, HeroP, HeroBtnWrapper, ArrowForward, ArrowRight } from './HeroElements'
import Video from '../../videos/waffleinto.mp4'
import { Button } from '../ButtonElement'


const HeroSection = () => {

    const [hover, setHover] = useState(false);

    const onHover = () => {
        setHover(!hover)
    }

    return ( 
        <HeroContainer id='home'>
             <HeroBg>
                 <VideoBg autoPlay loop muted src={Video} type='video/mp4' />
             </HeroBg>
             <HeroContent>
                 <HeroH1>Revolutionising NFTs</HeroH1>
                 <HeroP>
                 Community will always be at our heart here at WaffleNFT.{"\n"}
                 Our main goal is and always has been bringing REAL utility to our holders.{"\n"}
                 Giving back to WaffleNFTs Holders with DAILY chances to win BIG!{"\n"}
                 Within our Roadmap & Whitepaper we have  a plan to build an amazing project which will  bring fun and excitement to our community each and every day, no more slow and boring community forums.{"\n"}
                 Holding Daily/Weekly/Monthly Raffle draws and even bringing in WaffleCasino{"\n"} 




                 </HeroP>
                 <HeroBtnWrapper>
                     <Button to="signup" onMouseEnter={onHover} 
                                         onMouseLeave={onHover}
                                         primary="true"
                                         dark="true"
                                         smooth={true}
                                         duration={500}
                                         spy={true}
                                         exact='true'
                                         offset={-80}
                                      
                                         >
                         Download Whitepaper {hover ? <ArrowForward /> : <ArrowRight />}
                     </Button>
                 </HeroBtnWrapper>
             </HeroContent>
        </HeroContainer>
     );
}
 
export default HeroSection;
