	margin-top: 13px;
	width: fit-content;
`

export const ArrowForward = styled(MdArrowForward)`
     margin-left: 8px;
     font-size: 20px;
  
`

export const ArrowRight = styled(MdKeyboardArrowRight)`
     margin-left: 8px;
     font-size: 20px;

`

export const HeroImage = styled.div`
	width: 35%;
	padding-left: 20px;
	padding-right: 20px;
`
export const LeftContents = styled.div`
	width: 53%;	
`
export const HeroH3 = styled.h3`
	font-size: 38px;
	line-height: 57px;
	color: #140634;
	font-weight: bold;
	font-family: Poppins;
`
